package pers.linus.leetcode.subString;

import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.Test;
import pers.linus.leetcode.repeatword.Main;

import static org.junit.jupiter.api.Assertions.*;

class SolutionTest extends Main {
    private static Solution solution;

    @BeforeAll
    public static void beforeAll() {
        solution = new Solution();
    }

    @Test
    public void case1() {
        assertTrue(solution.checkInclusion("ab", "eidbaooo"));
    }

    @Test
    public void case2() {
        assertFalse(solution.checkInclusion("ab", "eidboaoo"));
    }

    @Test
    public void case3() {
        assertFalse(solution.checkInclusion("abcd", "abccde"));
    }

    @Test
    public void case4() {
        assertTrue(solution.checkInclusion("adc", "dcda"));
    }

    @Test
    public void case5() {
        assertFalse(solution.checkInclusion("hello", "ooolleoooleh"));
    }

    @Test
    public void case6() {
        assertFalse(solution.checkInclusion("rokx", "otrxvfszxroxrzdsltg"));
    }

    @Test
    public void case7() {
        System.out.println("");
        assertTrue(solution.checkInclusion("",
                ""));
        }
        }